mapper.select:
SELECT student.student_id, student.username, student.student_name, student.native_place, student.class_id, student.dorm_id, student.parents_phone, back_school_information.back_school_information_id, back_school_information.username, back_school_information.statistics_version_id, back_school_information.area_code, back_school_information.address, back_school_information.back_time, back_school_information.is_healthy, back_school_information.temperature, back_school_information.is_contact, back_school_information.is_leave, back_school_information.leave_reason, back_school_information.is_back_school, back_school_information.state, area.AREA_ID, area.AREA_CODE, area.CITY_CODE, area.AREA_NAME, area.SHORT_NAME, area.LNG, area.LAT, area.SORT, area.GMT_CREATE, area.GMT_MODIFIED, area.MEMO, area.DATA_STATE, city.CITY_ID, city.CITY_CODE, city.CITY_NAME, city.SHORT_NAME, city.PROVINCE_CODE, city.LNG, city.LAT, city.SORT, city.GMT_CREATE, city.GMT_MODIFIED, city.MEMO, city.DATA_STATE, city.TENANT_CODE, province.PROVINCE_ID, province.PROVINCE_CODE, province.PROVINCE_NAME, province.SHORT_NAME, province.LNG, province.LAT, province.SORT, province.GMT_CREATE, province.GMT_MODIFIED, province.MEMO, province.DATA_STATE, province.TENANT_CODE, dorm.dorm_id, dorm.dorm_area, dorm.dorm_num, class_table.class_table_id, class_table.specialty_id, class_table.class_table_name, class_table.is_graduate, specialty.specialty_id, specialty.college_id, specialty.specialty_name, college.college_id, college.college_name, statistics_version.statistics_version_id, statistics_version.statistics_version_name, statistics_version.enable FROM ((((((((( student JOIN back_school_information ON student.username = back_school_information.username ) JOIN statistics_version ON statistics_version.statistics_version_id = back_school_information.statistics_version_id ) JOIN area ON back_school_information.area_code = area.AREA_CODE ) JOIN city ON area.CITY_CODE = city.CITY_CODE ) JOIN province ON city.PROVINCE_CODE = province.PROVINCE_CODE ) JOIN dorm ON student.dorm_id = dorm.dorm_id ) JOIN class_table ON student.class_id = class_table.class_table_id ) JOIN specialty ON class_table.specialty_id = specialty.specialty_id ) JOIN college ON specialty.college_id = college.college_id )
and username = #{username}
and class_table_id = #{classTableId}
and class_table.specialty_id = #{specialtyId}
and college.college_id = #{collegeId}
and back_school_information.is_healthy = #{isHealthy}
and back_school_information.temperature > #{temperature}
and back_school_information.is_contact = #{isContact}
and back_school_information.is_leave = #{isLeave}
and back_school_information.is_back_school = #{isBackSchool}
and back_school_information.area_code = #{areaCode}
and city.city_code = #{cityCode}
and province.province_code = #{provinceCode}
